
$(function(){$("#register").submit(function(){$("#register").ajaxSubmit({success:function(a){a=JSON.parse(a);showNotify(a.message,a.status);if(a.status=="success"){window.setTimeout(function(){window.location=base_url+"auth/login"},1000)}}});return false})});function showNotify(b,a){thisNotify=UIkit.notify({message:b,status:a,timeout:5000,onClose:function(){clearTimeout(thisNotify.timeout)}})};