
$("body").on("click","#btn-add",function(a){a.preventDefault();if(localStorage.getItem("store")=="all"){showModalNotKeyboard("store")}else{$("#form").parsley().reset();$("#save_method").val("add");$("#id").val("");$.get(base_url+"cash/expenses/get_code",function(b){$("#code").val(b);$("#code").parent("div.md-input-wrapper").addClass("md-input-filled")});$("#amount").val(0);$("#date").val(date_now());$("#note").val("");$("#form .md-input").each(function(){if($(this).val().length==0){$(this).parent("div.md-input-wrapper").removeClass("md-input-filled")}else{$(this).parent("div.md-input-wrapper").addClass("md-input-filled")}});showModal("form")}});$("body").on("click",".btn-edit",function(a){a.preventDefault();$("#form").parsley().reset();$("#save_method").val("edit");var b=$(this).attr("data-id");$.ajax({url:current_url+"/get/"+b,method:"post",success:function(c){var d=JSON.parse(c);$("#id").val(d.id);$("#code").val(d.code);$("#date").val(d.date);$("#amount").val(d.amount);$("#note").val(d.note);$("#form .md-input").each(function(){if($(this).val().length>0){$(this).parent("div.md-input-wrapper").addClass("md-input-filled")}});showModal("form")}})});